import React, { useState } from 'react';

// React Hooks Challenge: Get Time on Refreshing and Clicking Button using useState Hook in Hindi

const App = () => {
  const newTime = new Date().toLocaleTimeString();

  const [ctime, setCtime] = useState(newTime);

  const IncCum = () => {
    setCtime(new Date().toLocaleTimeString());
  };
  return (
    <>
      <h1>{ctime}</h1>
      <button onClick={IncCum}>Click Me</button>
    </>
  );
};

export default App;
