import React from 'react';
import styled from 'styled-components';

// Variables & Nesting in Styled Components:

const App = () => {
  const Button = styled.button`
     {
      background-color: green;
      color: #fff;
      font-size: 16px;
      text-align: center;
      padding: 6px 30px;

      &:hover {
        background-color: yellow;
        color: #000;
      }
      &:active {
        background-color: red;
        color: #fff;
      }
    }
  `;

  const Para = styled.p`
    color: red;
    font-size: 16px;

    &:hover {
      background-color: yellow;
      color: #000;
    }
  `;
  return (
    <>
      <Button className="btn">Subcribe Shyam</Button>
      <Para>Shyam Tahput</Para>
    </>
  );
};

export default App;
